import java.util.*;		public class Valid_Data 		{		  public static void main(String[] ard)		  {			String id;			String first_name;			String last_name;			Scanner input1=new Scanner(System.in);			Scanner input2=new Scanner(System.in);			boolean correct1;			boolean correct2;			boolean correct3;			boolean stop=false;							System.out.println("Enter first name:");			first_name=input1.nextLine();			System.out.println("Enter last name:");			last_name=input2.nextLine();			System.out.println("Enter your regestration number:");			id=input2.nextLine();			correct1=id.matches("(FA|SP)\\d{2}-(B|M)(SE|CS|BA|CE|EE|PH)-\\d{3}");						correct2=first_name.matches("[A-Z][a-zA-Z]*");			correct3=last_name.matches("[A-Z][a-zA-Z]*");					if((correct1=true) &&(correct2=true) && (correct3=true))		{			System.out.println("Valid data entered.");			System.out.printf("%nStudent attributes:\n");			System.out.printf("%n%s:%s%n%s:%s%n%s:%s%n","First Name",first_name,"Last Name",last_name,"ID",id);			stop=true;		}		else		{			System.out.println("Invalid data entered!!");		}		   										  }		}